# body.json
{
  "version": "1.3.0",
  "layers": [
    {
      "type": "mapnik",
      "options": {
        "cartocss_version": "2.1.1",
        "cartocss": "#layer { polygon-fill: #FFF; }",
        "sql": "select * from european_countries_e",
        "interactivity": [
          "cartodb_id",
          "iso3"
        ]
      }
    }
  ]
}

curl -X POST -H "Content-Type: application/json" -d @body.json "https://username.carto.com/api/v2/sql"

# body.json
{
  "version": "0.0.1",
  "name": "template_name",
  "auth": {
    "method": "token",
    "valid_tokens": [
      "auth_token1",
      "auth_token2"
    ]
  },
  "placeholders": {
    "color": {
      "type": "css_color",
      "default": "red"
    },
    "cartodb_id": {
      "type": "number",
      "default": 1
    }
  },
  "layergroup": {
    "version": "1.7.0",
    "layers": [
      {
        "type": "cartodb",
        "options": {
          "cartocss_version": "2.3.0",
          "cartocss": "#layer { polygon-fill: <%= color %>; }",
          "sql": "select * from european_countries_e WHERE cartodb_id = <%= cartodb_id %>"
        }
      }
    ]
  },
  "view": {
    "zoom": 4,
    "center": {
      "lng": 0,
      "lat": 0
    },
    "bounds": {
      "west": -45,
      "south": -45,
      "east": 45,
      "north": 45
    },
    "preview_layers": {
      "0": true,
      "layer1": false
    }
  }
}

curl -X POST -H "Content-Type: application/json" -d @body.json "https://{username}.carto.com/api/v1/map/named?api_key={api_key}"

